def templatePath = testRunner.testCase.testSuite.getPropertyValue("templatePath")
def templateFileName = testRunner.testCase.getPropertyValue("TemplateFileName")
def FullFilePath = ""

if (templateFileName == null || templateFileName == "" ) 
	templateFileName = "createPerson.xml"

if (File.separator == '/') {
	FullFilePath = templatePath + "\\" +  templateFileName
} else { 
	FullFilePath = templatePath + File.separator +  templateFileName
}

testRunner.testCase.setPropertyValue("AbsoluteFilePath",FullFilePath)

def file = new File(FullFilePath)
assert file.exists() : "template file not found"

