dataSourceFileName = testRunner.testCase.testSuite.getPropertyValue("dataSourceFileName")

dataSourceAbosulteFilePath = dataSourcePath + "\\" + dataSourceFileName
log.info ("Source File Path: " +  dataSourceAbosulteFilePath)
