file = new File(dataSourceAbosulteFilePath)
assert file.exists() : "source file not found"


def lineNo = 1
def line
def lines = []
def values = []

def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def holder = ''

file.withReader { reader ->
	 while ((line = reader.readLine())!=null) {
            log.info ( "${lineNo}. ${line}" )
		  if (lineNo == 1) {
            	lines.push("${line}")
            } else
            {
            	values = "${line}".split(",") ;
            	testRunner.testCase.setPropertyValue("personID",values[0])
            	testRunner.testCase.setPropertyValue("firstName",values[1])
            	testRunner.testCase.setPropertyValue("lastName",values[2])
            	testRunner.runTestStepByName("CreatePerson")            			
            }
             lineNo++

	 }
}

