import groovy.sql.Sql;

def  con = Sql.newInstance("jdbc:oracle:thin:@localhost:1521:max1", "maximo", "maximo", "oracle.jdbc.driver.OracleDriver");
def res = con.rows("SELECT sysdate FROM dual")

log.info("Today Date" + res[0].sysdate.toString())

con.close()

def woStatus = [""] ;
def apprStatus = "APPR"
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def holder = "";
def isWOCreated = "";

for( i=0 ; ( i <= 10 && !(woStatus[0] == apprStatus) ) ; i++)
{
	log.info("WO Status: " + woStatus[0])
	log.info("Count of i: " + i)
	log.info("woStatus.equals(apprStatus)):"+(woStatus[0] == apprStatus)+" : "+ apprStatus + woStatus.toString() )

	if( i <= 10 && !(woStatus.toString().equals(apprStatus.toString()))  )
	{
		log.info("inside the for loop: if condition" )
		testRunner.runTestStepByName("getWOFromSR")
		holder = groovyUtils.getXmlHolder( "getWOFromSR#Response" )

		holder.declareNamespace("max","http://www.ibm.com/maximo")	
		//getNodeValues returns a list of values
		if ( holder != null) {
			isWOCreated = holder.getNodeValue( "//max:QueryMXWOResponse/max:MXWOSet/count(max:WORKORDER)")
			log.info("Count of Workorder " + isWOCreated)
			if (isWOCreated == "1") {
				woStatus = holder.getNodeValues( "//max:QueryMXWOResponse/max:MXWOSet/max:WORKORDER/max:STATUS")
			}
		}	
		Thread.currentThread().sleep(20000)	
		i = i + 1 ; 	
	} 
}

import groovy.sql.Sql;

def  con = Sql.newInstance("jdbc:oracle:thin:@localhost:1521:max1", "maximo", "maximo", "oracle.jdbc.driver.OracleDriver");

def wonum = testRunner.testCase.testSuite.getPropertyValue("WONUM")
def sr = testRunner.testCase.testSuite.getPropertyValue("SRID")

sql_query = "SELECT wonum FROM ORA_IFACE where WONUM='" + wonum + "' and origrecordid = '" + sr + "' and origrecordclass = 'SR'" ; 
def res = con.rows(sql_query)

assert res[0].wonum.toString() == wonum
log.info("WO: " + res[0].wonum.toString() +  " OutBounded Successfully!!" )

con.close()

